						current_count++;
						if (node is Branch)
							foreach (Node child in (node as Branch).Children)
								nodeCountFinder(child, ref current_count);
					};

				int _current_count = 0;
				nodeCountFinder(this._top, ref _current_count);
				return _current_count;
			}
		}

