			, (a, b) => _compare2(a, b) is Equal
			, (a, b) => _compare3(a, b) is Equal
			, (a, b) => _compare4(a, b) is Equal
			, (a, b) => _compare5(a, b) is Equal
			, (a, b) => _compare6(a, b) is Equal
