			internal Leaf(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4, Axis5, Axis6, Axis7> bounds, Branch parent, int index)
				: base(bounds, parent, index)
			{ }

			internal Leaf(Leaf leaf) : base(leaf)
			{
				Head = new Node(leaf.Head.Value, null);
				Node a = Head;
				Node b = leaf.Head;
				while (b is not null)
