		/// <param name="axis1">The coordinate along the 1D axis.</param>
		/// <param name="axis2">The coordinate along the 2D axis.</param>
		public void Update(Omnitree.Bound<Axis1> axis1, Omnitree.Bound<Axis2> axis2)
		{
