			else if (a.Min5.Exists && b.Max5.Exists && compare5(a.Min5.Value, b.Max5.Value) == CompareResult.Greater)
				return false;

			if (a.Max6.Exists && b.Min6.Exists && compare6(a.Max6.Value, b.Min6.Value) == CompareResult.Less)
