			if (bounds.Min1.Exists && compare1(vector.Axis1, bounds.Min1.Value) == CompareResult.Less)
				return false;
			else if (bounds.Max1.Exists && compare1(vector.Axis1, bounds.Max1.Value) == CompareResult.Greater)
