				if (collection_map.TryGet(index, out list))
				{
					list.Add(value);
				}
				else
				{
					if (parent_count < 100000)
						list = new ListArray<T>();
					else
						list = new ListLinked<T>();
					collection_map.Add(index, list);
					list.Add(value);
				}
			});

