		private int RemoveBase(
			Node node,
			Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> bounds,
			Predicate<T> where,
			Omnitree.SpatialCheck<Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4>, Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4>> spatialCheck)
		{
