		private bool StraddlesLines(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> bounds, Omnitree.Vector<Axis1, Axis2, Axis3, Axis4> vector)
		{
			if ((!bounds.Min1.Exists || (this._compare1(bounds.Min1.Value, vector.Axis1) != CompareResult.Greater)) &&
				(!bounds.Max1.Exists || (this._compare1(bounds.Max1.Value, vector.Axis1) != CompareResult.Less)))
				return true;
			if ((!bounds.Min2.Exists || (this._compare2(bounds.Min2.Value, vector.Axis2) != CompareResult.Greater)) &&
				(!bounds.Max2.Exists || (this._compare2(bounds.Max2.Value, vector.Axis2) != CompareResult.Less)))
				return true;
			if ((!bounds.Min3.Exists || (this._compare3(bounds.Min3.Value, vector.Axis3) != CompareResult.Greater)) &&
				(!bounds.Max3.Exists || (this._compare3(bounds.Max3.Value, vector.Axis3) != CompareResult.Less)))
				return true;
			if ((!bounds.Min4.Exists || (this._compare4(bounds.Min4.Value, vector.Axis4) != CompareResult.Greater)) &&
				(!bounds.Max4.Exists || (this._compare4(bounds.Max4.Value, vector.Axis4) != CompareResult.Less)))
				return true;
			return false;
		}

		/// <summary>Computes the child index that contains the desired dimensions.</summary>
		/// <param name="node">The branch .</param>
		/// <param name="vector">The dimensions to determine the child index.</param>
		/// <returns>The computed child index based on the coordinates relative to the center of the node.</returns>
		private int DetermineChildIndex(Omnitree.Vector<Axis1, Axis2, Axis3, Axis4> pointOfDivision, Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> bounds)
		{
			// make sure a child encapsulates the bounds (otherwise the bounds cannot move down the tree)
			if (StraddlesLines(bounds, pointOfDivision))
				return -1;

			int child = 0;
			if (!bounds.Min1.Exists || !(this._compare1(bounds.Min1.Value, pointOfDivision.Axis1) == CompareResult.Less))
				child += 1 << 0;
			if (!bounds.Min2.Exists || !(this._compare2(bounds.Min2.Value, pointOfDivision.Axis2) == CompareResult.Less))
				child += 1 << 1;
			if (!bounds.Min3.Exists || !(this._compare3(bounds.Min3.Value, pointOfDivision.Axis3) == CompareResult.Less))
				child += 1 << 2;
			if (!bounds.Min4.Exists || !(this._compare4(bounds.Min4.Value, pointOfDivision.Axis4) == CompareResult.Less))
				child += 1 << 3;
			return child;
		}

		/// <summary>Converts a branch back into a leaf when the count is reduced.</summary>
		/// <param name="parent">The parent to shrink a child of.</param>
		/// <param name="child">The index of the child to shrink.</param>
		private void ShrinkChild(Node parent, int child_index)
		{
			Node leaf;
			Node removal = null;
			if (parent == null) // top of tree
			{
				removal = this._top;
				leaf = new Node(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4>.None, null, -1);
				this._top = leaf;
			}
			else // non-top branch
			{
				removal = parent[child_index];
				leaf = new Node(removal.Bounds, removal.Parent, removal.Index);
				parent[child_index] = leaf;
			}

			this.Stepper((T step) => { leaf.Add(step); }, removal);
		}

		/// <summary>Reduces the counts of all the parents of a given node by a given amount.</summary>
		/// <param name="parent">The starting parent of the reduction.</param>
		/// <param name="reduction">The amount to reduce the parent counts by.</param>
		private void ReduceParentCounts(Node parent, int reduction)
		{
			IncreaseParentCounts(parent, -reduction);
		}

		/// <summary>Increases the counts of all the parents of a given node by a given amount.</summary>
		/// <param name="parent">The starting parent of the increase.</param>
		/// <param name="increase">The amount to increase the parent counts by.</param>
		private void IncreaseParentCounts(Node parent, int increase)
		{
			Node looper = parent;
			while (looper != null)
			{
				looper.Count += increase;
				looper = looper.Parent;
			}
		}

		/// <summary>Checks a node for inclusion (overlap) between two bounds.</summary>
		/// <returns>True if the spaces overlap; False if not.</returns>
		private bool InclusionCheck(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> a, Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> b)
		{

			if (a.Max1.Exists && b.Min1.Exists && this._compare1(a.Max1.Value, b.Min1.Value) == CompareResult.Less)
				return false;
			else if (a.Min1.Exists && b.Max1.Exists && this._compare1(a.Min1.Value, b.Max1.Value) == CompareResult.Greater)
				return false;

			if (a.Max2.Exists && b.Min2.Exists && this._compare2(a.Max2.Value, b.Min2.Value) == CompareResult.Less)
				return false;
			else if (a.Min2.Exists && b.Max2.Exists && this._compare2(a.Min2.Value, b.Max2.Value) == CompareResult.Greater)
				return false;

			if (a.Max3.Exists && b.Min3.Exists && this._compare3(a.Max3.Value, b.Min3.Value) == CompareResult.Less)
				return false;
			else if (a.Min3.Exists && b.Max3.Exists && this._compare3(a.Min3.Value, b.Max3.Value) == CompareResult.Greater)
				return false;

			if (a.Max4.Exists && b.Min4.Exists && this._compare4(a.Max4.Value, b.Min4.Value) == CompareResult.Less)
				return false;
			else if (a.Min4.Exists && b.Max4.Exists && this._compare4(a.Min4.Value, b.Max4.Value) == CompareResult.Greater)
				return false;

			return true;
		}

		/// <summary>Checks if a space encapsulates a point.</summary>
		/// <returns>True if the space encapsulates the point; False if not.</returns>
		private bool EncapsulationCheck(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> bounds, Omnitree.Vector<Axis1, Axis2, Axis3, Axis4> vector)
		{
			// if the location is not outside the bounds, it must be inside

			if (bounds.Min1.Exists && this._compare1(vector.Axis1, bounds.Min1.Value) == CompareResult.Less)
				return false;
			else if (bounds.Max1.Exists && this._compare1(vector.Axis1, bounds.Max1.Value) == CompareResult.Greater)
				return false;

			if (bounds.Min2.Exists && this._compare2(vector.Axis2, bounds.Min2.Value) == CompareResult.Less)
				return false;
			else if (bounds.Max2.Exists && this._compare2(vector.Axis2, bounds.Max2.Value) == CompareResult.Greater)
				return false;

			if (bounds.Min3.Exists && this._compare3(vector.Axis3, bounds.Min3.Value) == CompareResult.Less)
				return false;
			else if (bounds.Max3.Exists && this._compare3(vector.Axis3, bounds.Max3.Value) == CompareResult.Greater)
				return false;

			if (bounds.Min4.Exists && this._compare4(vector.Axis4, bounds.Min4.Value) == CompareResult.Less)
				return false;
			else if (bounds.Max4.Exists && this._compare4(vector.Axis4, bounds.Max4.Value) == CompareResult.Greater)
				return false;

			return true;
		}

		/// <summary>Checks if a space (left) encapsulates another space (right).</summary>
		/// <returns>True if the left space encapsulates the right; False if not.</returns>
		private bool EncapsulationCheck(Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> a, Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> b)
		{
			if ((a.Min1.Exists && !b.Min1.Exists) || (a.Min2.Exists && !b.Min2.Exists) || (a.Min3.Exists && !b.Min3.Exists) || (a.Min4.Exists && !b.Min4.Exists))
				return false;
			if ((a.Max1.Exists && !b.Max1.Exists) || (a.Max2.Exists && !b.Max2.Exists) || (a.Max3.Exists && !b.Max3.Exists) || (a.Max4.Exists && !b.Max4.Exists))
				return false;

			if (b.Min1.Exists && a.Min1.Exists && this._compare1(a.Min1.Value, b.Min1.Value) != CompareResult.Less)
				return false;
			if (b.Max1.Exists && a.Max1.Exists && this._compare1(a.Max1.Value, b.Max1.Value) != CompareResult.Greater)
				return false;

			if (b.Min2.Exists && a.Min2.Exists && this._compare2(a.Min2.Value, b.Min2.Value) != CompareResult.Less)
				return false;
			if (b.Max2.Exists && a.Max2.Exists && this._compare2(a.Max2.Value, b.Max2.Value) != CompareResult.Greater)
				return false;

			if (b.Min3.Exists && a.Min3.Exists && this._compare3(a.Min3.Value, b.Min3.Value) != CompareResult.Less)
				return false;
			if (b.Max3.Exists && a.Max3.Exists && this._compare3(a.Max3.Value, b.Max3.Value) != CompareResult.Greater)
				return false;

			if (b.Min4.Exists && a.Min4.Exists && this._compare4(a.Min4.Value, b.Min4.Value) != CompareResult.Less)
				return false;
			if (b.Max4.Exists && a.Max4.Exists && this._compare4(a.Max4.Value, b.Max4.Value) != CompareResult.Greater)
				return false;

			return true;
		}

		/// <summary>Checks for equality between two locations.</summary>
		/// <returns>True if equal; False if not;</returns>
		private bool EqualsCheck(Omnitree.Vector<Axis1, Axis2, Axis3, Axis4> a, Omnitree.Vector<Axis1, Axis2, Axis3, Axis4> b)
		{

			if (!this._equate1(a.Axis1, b.Axis1))
				return false;

			if (!this._equate2(a.Axis2, b.Axis2))
				return false;

			if (!this._equate3(a.Axis3, b.Axis3))
				return false;

			if (!this._equate4(a.Axis4, b.Axis4))
				return false;

			return true;
		}

		/// <summary>Gets the nearest parent that encapsulates a location.</summary>
		/// <param name="node">The starting node to find the encapsulating parent of the location.</param>
		/// <param name="bounds">The bounds to get the encapsulating parent from.</param>
		/// <returns>The nearest node that encapsulates the given location.</returns>
		private Node GetEncapsulationParent(Node node, Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> bounds)
		{
			while (node != null && !EncapsulationCheck(node.Bounds, bounds))
				node = node.Parent;
			return node;
		}

		private Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4> GetBoundings(T value)
		{
			Omnitree.Bound<Axis1> min1; Omnitree.Bound<Axis1> max1;
			Omnitree.Bound<Axis2> min2; Omnitree.Bound<Axis2> max2;
			Omnitree.Bound<Axis3> min3; Omnitree.Bound<Axis3> max3;
			Omnitree.Bound<Axis4> min4; Omnitree.Bound<Axis4> max4;
			this._getBounds(value,
				out min1, out max1,
				out min2, out max2,
				out min3, out max3,
				out min4, out max4);
			return new Omnitree.Bounds<Axis1, Axis2, Axis3, Axis4>(
				min1, max1,
				min2, max2,
				min3, max3,
				min4, max4);
		}

		#endregion

		#endregion
	}

