		private void Stepper(Step<T> step, Node node)
		{
			Node.ValueNode list = node.Head;
			while (list != null)
			{
				step(list.Value);
				list = list.Next;
			}
			if (node.Children != null)
				foreach (Node child in node.Children)
					this.Stepper(step, child);
		}

